<script type="text/javascript">
/*!
 * classie - class helper functions
 * from bonzo https://github.com/ded/bonzo
 *
 * classie.has( elem, 'my-class' ) -> true/false
 * classie.add( elem, 'my-new-class' )
 * classie.remove( elem, 'my-unwanted-class' )
 * classie.toggle( elem, 'my-class' )
 */

/*jshint browser: true, strict: true, undef: true */

( function( window ) {

'use strict';

// class helper functions from bonzo https://github.com/ded/bonzo

function classReg( className ) {
  return new RegExp("(^|\\s+)" + className + "(\\s+|$)");
}

// classList support for class management
// altho to be fair, the api sucks because it won't accept multiple classes at once
var hasClass, addClass, removeClass;

if ( 'classList' in document.documentElement ) {
  hasClass = function( elem, c ) {
    return elem.classList.contains( c );
  };
  addClass = function( elem, c ) {
    elem.classList.add( c );
  };
  removeClass = function( elem, c ) {
    elem.classList.remove( c );
  };
}
else {
  hasClass = function( elem, c ) {
    return classReg( c ).test( elem.className );
  };
  addClass = function( elem, c ) {
    if ( !hasClass( elem, c ) ) {
      elem.className = elem.className + ' ' + c;
    }
  };
  removeClass = function( elem, c ) {
    elem.className = elem.className.replace( classReg( c ), ' ' );
  };
}

function toggleClass( elem, c ) {
  var fn = hasClass( elem, c ) ? removeClass : addClass;
  fn( elem, c );
}

window.classie = {
  // full names
  hasClass: hasClass,
  addClass: addClass,
  removeClass: removeClass,
  toggleClass: toggleClass,
  // short names
  has: hasClass,
  add: addClass,
  remove: removeClass,
  toggle: toggleClass
};

})( window );
</script>
<script type="text/javascript">
(function () {
    if (!document.getElementsByClassName) {
        var disclaimer = document.getElementById('disclaimer');
        disclaimer.style.display = 'block';
        return;
    }




    var headList = document.getElementsByTagName("h2");
    for (var h= 0, head; head = headList[h]; h++) {
        head.heading = 1;
    }
    headList = document.getElementsByTagName("h5");
    for (h= 0, head; head = headList[h]; h++) {
        head.heading = 1;
    }

    var container = document.getElementById("tree");

    // For each sutra, apply anno view function.
    var sutraList = container.getElementsByClassName('sutra');
    for (var b = 0, str; str = sutraList[b]; b++) {
        str.onclick = function () {
            if (!this.nextElementSibling.anno) return;
            if (this.nextElementSibling.style.display == 'none' || this.nextElementSibling.style.display == '') {
                this.nextElementSibling.style.display = 'block';
            } else {
                this.nextElementSibling.style.display = 'none';
            }
        };
        str.sutra = 1;
    }

    // Dblclick to close anno.
    var annoList = container.getElementsByClassName('annotation');
    for (b = 0, str; str = annoList[b]; b++) {
        str.ondblclick = function () {
            this.style.display = 'none';
        };
        str.anno = 1;
    }

    var factor = 8;
    if (screen.width < 640) {
        factor = 2;
    }

    // For each index, apply indent and assign click funciton.
    var indexList = container.getElementsByClassName('index');
    for (var a = 0, idx; idx = indexList[a]; a++) {
        var sn = 0;
        switch (idx.innerHTML.charAt(0)) {
            case '甲': sn = 1;break;
            case '乙': sn = 2; break;
            case '丙': sn = 3; break;
            case '丁': sn = 4; break;
            case '戊':
            case '戉': sn = 5; break;
            case '己':
            case '已': sn = 6; break;
            case '庚': sn = 7; break;
            case '辛': sn = 8; break;
            case '壬': sn = 9; break;
            case '癸': sn = 10; break;
            case '子': sn = 11; break;
            case '丑': sn = 12; break;
            case '寅': sn = 13; break;
            case '卯': sn = 14; break;
            case '辰': sn = 15; break;
            case '巳': sn = 16; break;
            case '午': sn = 17; break;
            case '未': sn = 18; break;
            case '申': sn = 19; break;
            case '酉': sn = 20; break;
            case '戌': sn = 21; break;
            case '亥': sn = 22; break;
            case '地': sn = 23; break;
            case '水': sn = 24; break;
            case '火': sn = 25; break;
            case '风': sn = 26; break;
            case '空': sn = 27; break;
            case '根': sn = 28; break;
            case '识': sn = 29; break;
            default:
                console.log('Wrong index ' + idx.innerHTML);
        }
        idx.style.textIndent = sn*factor + 'px';
        idx.indent = sn;
        idx.pos = a;

        idx.onclick = function () {
            var toClose = this.className.split(' ')[1] === 'index-mark-open';
            var depth = this.indent;
            var next = this;
            if (toClose) {
                next.className = 'index index-mark-close';
                while(next = next.nextElementSibling) {
                    if (next.indent && next.indent <= depth) break;
                    if (next.indent) {
                        next.className = 'index index-mark-close';
                        next.style.display = 'none';
                    } else if (next.sutra || next.heading || next.anno) {
                        next.style.display = 'none';
                    }
                }
            } else {
                next.className = 'index index-mark-open';
                while(next = next.nextElementSibling) {
                    if (next.indent && next.indent <= depth) break;
                    if (next.indent) {
                        next.className = 'index index-mark-open';
                        next.style.display = 'block';
                    } else if (next.sutra || next.heading) {
                        next.style.display = 'block';
                    }
                }
            }
        }
    }
})();
</script>
</body>
